var d = [];
var lazy = $('').lazyRule(() => {
    try {
        var html = request(input).match(/url: '(.*?)'/)[1]
        if (/mp3|m4a/.test(html)) {
            var play = html
        } else {
            var play = JSON.parse(request('https://www.hifini.com/' + html, {
                withHeaders: true,
                redirect: false
            })).headers.location[0];
        }
        return /mp3|m4a/.test(play) ? play : JSON.parse(request(play, {
            withHeaders: true,
            redirect: false
        })).headers.location[0]
    } catch (e) {
        return input
    }
});
var list = pdfa(getResCode(), '.list-unstyled&&li:not(.top_3)');
for (var j in list) {
    d.push({
        title: pdfh(list[j], '.subject&&Text'),
        desc: pdfh(list[j], '.haya-post-info-username&&Text'),
        img: 'https://www.hifini.com/' + pdfh(list[j], 'img&&src'),
        url: 'https://www.hifini.com/' + pdfh(list[j], '.subject&&a&&href') + lazy
    });
}
setResult(d);

