js:
var res = {};
var items = [];
var host = 'https://www.dygod.net';
var pos = MY_URL.split('###')[1];
var pa = MY_URL.split('###')[2];
//log(pa);

if (pa == 'page=0') {
    var src = fetch(host + '/e/search/index.php', {
        headers: {
            'Referer': 'https://www.dygod.net',
            'Content-Type': 'application/x-www-form-urlencoded; charst=gb2312'
        },
        body: pos,
        method: 'POST'
    });
    var html = src;
    //log(html);
    putMyVar('silifany', '');
    if (html.indexOf('下一页') != -1) {
        var pag = host + parseDomForHtml(src, '.x:has(:contains(尾页))&&a,-1&&href');
        putMyVar('silifany', pag);
    }
}
//setError(pag);

//判断取网页源码
if (pa == 'page=0') {
    html = src;
} else {
    html = fetch(getMyVar('silifany').replace(/page=\d*/, pa), {})
};

//列表显示开始
var list = parseDomForArray(html, '.co_content8&&table');
for (var i in list) {
    var title = parseDomForHtml(list[i], "b&&Text");
    var url = parseDomForHtml(list[i], "a,-1&&href");
    var cont = parseDomForHtml(list[i], "tr,-1&&Text");
    items.push({
        title: title,
        url: host + url+';get;gb2312',
        content: cont
    });
}
res.data = items;
setSearchResult(res);

