        url: MY_URL.replace("/api", ""),
        col_type: "movie_1_vertical_pic",
        desc: json2.introduction,
        pic_url: json2.posterLink + "@Referer=" + json2.doubanLink
    });
    d.push({
        col_type: "big_blank_block"
    });
    d.push({
        col_type: "big_blank_block"
    });
} catch (e) {}
let json = JSON.parse(getResCode()).data.list;
if (json.length <= 0) {
    d.push({
        title: "没有资源，可以看看留言板检索结果",
        url: MY_URL.replace("/api", ""),
        col_type: "text_1",
        desc: "点击查看原网页",
        pic_url: ""
    });
    setResult(d)
} else {
    let season = getMyVar("s", json[0].season_cn);
    let git = {};
    for (let it of json) {
        if (!it.items) {
            continue
        }

        d.push({
            title: season == it.season_cn ? "‘‘" + it.season_cn + "’’" : it.season_cn,
            url: $("#noLoading#").lazyRule((c) => {
                putMyVar("s", c);
                refreshPage();
                return "hiker://empty"
            }, it.season_cn),
            col_type: "scroll_button",
            desc: "",
            pic_url: ""
        });
        if (season == it.season_cn) {
            git = it;
        }
    }
    d.push({
        col_type: "blank_block"
    });
    let it = git;
    let items = it.items;
    let keys = Object.keys(items);
    let def = keys[0];
    //第一次进入自动选中有结果的选项
    if (getMyVar("ite") == "") {
        for (let i of keys) {
            if (!items[i]) {
                continue;
            }
            let ok = false;
            for (let it2 of items[i]) {
                if (it2 && it2.files && it2.files.length > 0) {
                    def = i;
                    ok = true;
                    break
                }
            }
            if (ok) break;
        }
    }
    let ite = getMyVar("ite", def);
    let gi = "";
    for (let i of keys) {
        d.push({
            title: i == ite ? "‘‘" + i + "’’" : i,
            url: $("#noLoading#").lazyRule((i) => {
                putMyVar("ite", i);
                refreshPage();
                return "hiker://empty"
            }, i),
            col_type: "scroll_button",
            pic_url: ""
        });
        if (i == ite) {
            gi = i;
        }
    }
    d.push({
        col_type: "line_blank"
    });

    let i = gi;
    let ts = items[i];
    for (let i3 of ts) {
        if (!i3.files) {
            continue
        }
        d.push({
            title: i3.name + "  " + i3.size,
            url: "confirm://这里只是名字展示，请点击下面的资源按钮来播放，无法播放可以长按下载唤起第三方软件来播放或下载.js:''",
            col_type: "text_1",
            desc: "",
            pic_url: ""
        });

        let files = i3.files;
        for (let i2 of files) {
            d.push({
                title: i2["way_cn"],
                url: i2["address"],
            });
        }
        d.push({
            col_type: "blank_block"
        });

        d.push({
            col_type: "line_blank"
        });

    }

    setResult(d);
}

