let layout = [];
let searchTypeList = $.require("searchType"),
    serverTypeList = $.require("serverType");

let keyword = decodeURIComponent(getParam("keyword","")),
    searchType = getParam("searchType") || searchTypeList[0].value,
    serverType = getParam("serverType") || serverTypeList[0].value;
let page = Number(getMyVar("page", "0")) + MY_PAGE;
if(keyword){
$.require("搜索记录").setHistory(keyword);
}
if (MY_PARAMS.isRefresh) {
    addListener("onClose", () => {
        refreshPage();
    });
}
addListener("onClose",()=>{
    clearMyVar("page");
});
function load() {
    let data = JSON.parse(post("https://dongmanhuayuan.myheartsite.com/api/acg/search", {
        body: buildUrl("", {
            keyword: keyword,
            page: page,
            searchType: searchType,
            serverType: serverType
        }),
        timeout:8000
    }));
    let list = data.data.searchData;
    if (MY_PAGE === 1) {
        layout.push({
            col_type: "text_2",
            url: "hiker://empty",
            title: "总计" + data.data.totalNum,
            extra: {
                lineVisible: false
            }
        });
        let pageNum = Math.ceil(data.data.totalNum / 80);
        layout.push({
            col_type: "text_2",
            url: $("", "最大:" + pageNum).input((m) => {
                let n = Number(input);
                if (Number.isNaN(n)) {
                    return "toast://请输入数字";
                }
                if (n > m || n < 1) {
                    return "toast://数字不对哦";
                }
                putMyVar("page", (n - 1) + "");
                refreshPage();
                return "hiker://empty";
            }, pageNum),
            title: "共" + pageNum + "页",
        });

    }
    for (let item of list) {
        layout.push({
            title: "‘‘" + (item.group || "") + "’’\t" + item.title,
            desc: "““””<strong>时间：</strong>" + item.date + "\n<strong>类型：</strong>" + item.type + "\t\t<strong>大小：</strong>" + item.size,
            col_type: "text_1",
            url: "hiker://page/详细",
            extra: {
                body: "link=" + item.link + "&id=" + (item.id || ""),
                pageTitle: item.title,
            }
        });
    }
    if (list.length === 0) {
        layout.push({
            title: "““””<font color='gray'>没有了哦<font>",
            col_type: "text_center_1",
            url: "hiker://empty",
        });
    }else{
        setPageTitle("「" + (keyword||"最近更新") + "」#" + page);
    }
}
try {
    if(MY_PAGE===1){
        showLoading("正在检索服务器...");
        setPageTitle("正在检索:" + (keyword||"最近更新"));
    }
    load();

    hideLoading();
} catch (e) {
    hideLoading();
    log(e.toString());
    layout.push({
        title: "““””<font color='gray'>没有了哦<font>",
        col_type: "text_center_1",
        url: "hiker://empty",
    });
}

setResult(layout);

