let Erji = {
    close: function(list, sorts) {
        addListener('onClose', $.toString((list, sorts) => {
            list.map(x => {
                clearMyVar(x + 'fold');
                clearMyVar(x);
            })
            for (let i = 0; i < sorts; i++) {
                clearMyVar('playlist_' + i);
            }
        }, list, sorts))
    },
    small: function(str, color) {
        return '‘‘’’<small><font color="' + (color || '') + '">' + str + '</font></small>';
    },

//线路
    setTabs: function(tabs, vari, shsort) {
        shsort = shsort ? shsort : 'shsort';
        var title = (getMyVar(shsort, '0') == '1') ? '<b><span style="color: #FF0000">逆序</span></b>' : '<b><span style="color: #1aad19">正序</span></b>'
        d.push({
            title: vari + ':' + title,
            desc: '共' + tabs.length + '条线路',
            url: $('#noLoading#').lazyRule((shsort) => {
                let conf = getMyVar(shsort, '0');
                if (conf == '1') {
                    putMyVar(shsort, '0');
                } else {
                    putMyVar(shsort, '1');
                }
                refreshPage()
                return 'toast://切换排序成功'
            }, shsort),
            col_type: 'avatar',
            pic_url: 'https://cdn4.iconfinder.com/data/icons/buno-social-networks/32/youtube_video_play-128.png',
        })
        var 折叠 = '““””<b>'  +  '☷'.fontcolor("#D3D3D3");
        var 展开 = '““””<b>'  +  '☰'.fontcolor("#FFA500");
        var foldname = vari + 'fold';
        var fold = getMyVar(foldname, '1');

        d.push({
            title:  fold ===  '0'  ?   折叠  :  展开,
            col_type: 'scroll_button',
            url: $('#noLoading#').lazyRule((foldname, fold) => {
                putMyVar(foldname,  fold  ===  '1'  ?  '0'  :  '1');
                refreshPage();
                return 'toast://' + (fold  ===  '1'  ?  '收起'  :  '展开');
            }, foldname, fold)
        })
        for (var i = 0; i < tabs.length; i++) {
            var url = "#noLoading#@lazyRule=.js:putMyVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！';'#noHistory#hiker://empty'";
            d.push({
                title: getMyVar(vari, '0') == i ? '““' + tabs[i] + '””' : tabs[i],
                url: url,
                col_type: 'scroll_button'
            })
        }
    },
    setLists: function(lists, index, shsort) {
        shsort = shsort ? shsort : 'shsort';
        var list = lists[index];
        if (getMyVar(shsort) == '1' && lists.length > 0) {
            var list = lists[index].reverse();
        }
        for (var j in list) {
            d.push({
                title: list[j].title,
                url: list[j].url,
                col_type: list.length > 3 ? 'text_4' : 'text_2',
                extra: {
                    id: list[j].url.replace(/@lazyRule[\s\S]+/g, ''),
                }
            });
        }
    },
    getTabs: function(parse) {
        var tabs = [];
        var arts = pdfa(html, parse.线路);
        var 线路名 = parse.线路名称;
        var 替换函数 = '';
        if (parse.线路名称.includes('@@')) {
            线路名 = parse.线路名称.split('@@')[0];
            替换函数 = parse.线路名称.split('@@')[1];
        }
        for (var i in arts) {
            var linename = pdfh(arts[i], 线路名);
            if (替换函数 != '') {
                linename = eval('linename' + 替换函数);
            }
            tabs.push(linename);
        }

        return tabs;
    },
    getLists: function(parse) {
        var lists = [];
        var conts = pdfa(html, parse.选集);
        var 选集名称 = parse.选集名称 ? parse.选集名称 : 'a&&Text';
        var 选集链接 = parse.选集链接 ? parse.选集链接 : 'a&&href';
        var 解析 = parse.解析 ? parse.解析 : '';
        var temp = [];
        for (var i in conts) {
            if (parse.选集列表 != undefined) {
                lists.push(pdfa(conts[i], parse.选集列表).map(x => {
                    return {
                        title: pdfh(x, 选集名称),
                        url: pd(x, 选集链接) + 解析,
                    }
                }))
            } else {
                var url = pd(conts[i], 选集链接);
                if (!选集链接.includes('href')) {
                    url = pdfh(conts[i], 选集链接);
                }
                temp.push({
                    title: pdfh(conts[i], 选集名称),
                    url: url + 解析,
                })
            }
        }
        let 倒序 = parse.倒序 ? parse.倒序 : false;
        var results = lists.length ? lists : [temp];
        if (倒序) {
            results = results.map(x => x.reverse());
        }
        return results;
    },
    getPlayers: function(parse) {
        this.close(parse.map(x => x.类型), parse.length);
        parse.forEach((item, i) => {
            var tabs = ['主线'];
            if (item.线路 != undefined) {
                tabs = this.getTabs(item);
            }
            var lists = this.getLists(item);
            if (lists.some(x => x.length > 0)) {
                this.setTabs(tabs, item.类型, 'playlist_' + i);
            }

            if (getMyVar(item.类型 + 'fold', '1') == '1') {
                this.setLists(lists, getMyVar(item.类型, '0'), 'playlist_' + i);
            }
            d.push({
                col_type: 'blank_block'
            })
        })
    },
}

