function urlParse(u) {
    var dict = {}
    dict.Source = u;
    let params = (url) => {
        let queryParams = {};
        let reg = /([^?=&]+)=([^?=&]+)/g;
        url.replace(reg, function() {
            queryParams[arguments[1]] = arguments[2];
        })
        return queryParams
    }
    var javaUrl = JavaImporter(java.net.URL);
    with(javaUrl) {
        var url = new URL(u);
