var d = [];
var getRangeColors = function() {       
    return '#' + ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);     
}

const 分类颜色 = getRangeColors()
const 大类定位 = '.navbar-items'
const 拼接分类 = 'body&&.module-item-box||.module-tab-week'
const 小类定位 = 'body&&a:not(:matches(首页|专题|APP))'
const 分类标题 = 'a&&Text'
const 分类链接 = 'a&&href'
MY_URL = MY_URL.replace('hiker://empty##', '')

let true_url = getMyVar("url", MY_URL)
const page = MY_PAGE
if (getMyVar("url")) {
    true_url = true_url.replace(/list\/(\d+).*/ ,'v-show/$1-----------.html').replace(/---(\d+)?\.html/, page + '---$1' + '.html');
}
log(true_url)
var html = request(true_url)

eval(JSON.parse(fetch('hiker://page/sm', {})).rule);
var tabs = []
var lists = []
pdfa(html, '.module-heading&&span').forEach(data => {   
    tabs.push(pdfh(data, 'span&&Text').replace('周',''))
})
pdfa(html, 'body&&.module-items').forEach(data => {
    lists.push(pdfa(data, 'body&&a'))
})
if (/week/.test(true_url)&&MY_PAGE == 1){
function setTabs(tabs, vari) {
for (var i = 0; i < tabs.length; i++) {
        var url = "#noLoading#@lazyRule=.js:putVar('" + vari + "', '" + i + "');refreshPage(false);'toast://切换成功！';'#noHistory#hiker://empty'";
        d.push({
            title: getVar(vari, '0') == i ? '““' + tabs[i] + '””' : tabs[i],
            url: url,
            col_type: 'flex_button'
        })
    }
}

function setLists(lists, index) {
        var lists = lists[index]; 
log(lists)
for (var j in lists) {
    d.push({
        title: pdfh(lists[j], 'a&&title'),
        desc: pdfh(lists[j], '.module-item-note&&Text'),
        img: pd(lists[j], '.lazyload&&data-original') + '@Referer=',
        url: pd(lists[j], 'a&&href') + '#immersiveTheme#'
    });
}
}
setTabs(tabs, MY_URL);
setLists(lists, getVar(MY_URL, '0'));
}else{
//一级页面
var list = pdfa(html, '.module-items&&.module-card-item||a');
for (var j in list) {
    d.push({
        title: pdfh(list[j], 'img&&alt'),
        desc: pdfh(list[j], '.module-item-note&&Text'),
        img: pd(list[j], '.lazyload&&data-original') + '@Referer=',
        url: pd(list[j], 'a&&href') + '#immersiveTheme#'
    });
}
}
