detail_find_rule:
js:
var d = [];
var html = getResCode();
eval(JSON.parse(fetch('hiker://page/lazy', {})).rule);
var 线路 = '#y-playList&&span';
var 线路名 = 'Text';
var 选集 = 'body&&.module-list';
var 选集列表 = 'body&&a';
var tabs = []
var lists = []
pdfa(html, 线路).forEach(data => {   
    tabs.push(pdfh(data, 线路名))
})
pdfa(html, 选集).forEach(data => {
    lists.push(pdfa(data, 选集列表))
})
try {
    d.push({
        title: pdfh(html, '.module-info-item,1&&Text').substring(0, 18) +
            '\n' +
            '\n' +
                  pdfh(html, '.module-info-item,2&&Text').substring(0, 18),
        desc: pdfh(html, '.module-info-item,4&&Text'),
        img: pd(html,
            '.lazyload&&data-original') + '@Referer=',
        url: MY_URL,
        col_type: 'movie_1_vertical_pic_blur',
        extra: {
            gradient: true
        }
    });
} catch (e) {
    d.push({
        img: pd(html, '') + '@Referer=',
        url: MY_URL,  
        col_type: 'movie_1_vertical_pic_blur',
    });
}
var 简介 = pdfh(html, '.module-info-item&&Text')
d.push({
    title: '‘‘’’<small><font color="#ff148e8e">简介：' + 简介.substr(0, 55) + '...</font><small><font color="red">详情</font></small></small>',
    url: 'hiker://empty#' + '\n' + 简介 +'#noHistory#' + `@rule=js: var d = [];d.push({title:'影片简介：'+ MY_URL.split('#')[1],col_type: 'long_text'}); setResult(d);`,
    col_type: 'text_1'
});
addListener("onClose", $.toString(() => {
    clearMyVar("lists");
}));
storage0.putMyVar("lists", lists);
var list = lists[getMyVar(MY_URL, '0')];
var 排序 = getMyVar(MY_URL + "章节排序", '#1aad19">正序');
var 提示 = 排序 == '#FF0000">倒序' ? '#1aad19">正序' : '#FF0000">倒序';
var 排序名 = '““””<b><span style="color:排序</span></b>'

d.push({
    title: '““””<b><span style="color: ' + 排序 + '</span></b>',
    url: $("#noLoading#").lazyRule((tabs, 排序名, lazy, d) => {
        var lists = storage0.getMyVar("lists");
        var 章节 = lists[getMyVar(MY_URL, '0')];
        var 排序 = getMyVar(MY_URL + "章节排序", '#1aad19">正序');
        var 提示 = 排序 == '#FF0000">倒序' ? '#1aad19">正序' : '#FF0000">倒序';
        updateItem({
            extra: {
                id: MY_URL + "_排序",
            },
            title: 排序名.replace('排序', 提示)
        })
        if (排序 == '#1aad19">正序') {
            章节.reverse()
        }
        let cp = 章节.map((data, ssid) => {
            return {
                title: pdfh(data, 'a&&Text'),
                url: pd(data, 'a&&href') + lazy,
                col_type: 章节.length > 3 ? 'text_4' : 'text_2',
                extra: {
                    cls: MY_URL + "_选集",
                    id: pd(data, 'a&&href')
                }
            };
        });
        deleteItemByCls(MY_URL + "_选集");
        addItemBefore(MY_URL + "footer", cp);
        putMyVar(MY_URL + "章节排序", 提示)
        return "hiker://empty"
    }, tabs, 排序名, lazy, d[0]),
    col_type: 'flex_button',
    extra: {
        id: MY_URL + "_排序",
    }
})

tabs.forEach((data, id) => {
    d.push({
        title: getMyVar(MY_URL, '0') == id ? '““' + data + '””' : data,
        url: $("#noLoading#").lazyRule((线路, lazy, id) => {
            var lists = storage0.getMyVar("lists");
            var 排序 = getMyVar(MY_URL + "章节排序", '#1aad19">正序');
            var 提示 = 排序 == '#FF0000">倒序' ? '#1aad19">正序' : '#FF0000">倒序';
            线路.forEach((data, xlid) => {
                updateItem({
                    title: id == xlid ? '““' + data + '””' : data,
                    extra: {
                        id: MY_URL + "_线路" + xlid
                    }
                });
            })
            putMyVar(MY_URL, id)
            var 章节 = lists[getMyVar(MY_URL, '0')];
            if (排序 == '#FF0000">倒序') {
                章节.reverse()
            }
            let cp = 章节.map((data, ssid) => {
                return {
                    title: pdfh(data, 'a&&Text'),
                    url: pd(data, 'a&&href') + lazy,
                    col_type: 章节.length > 3 ? 'text_4' : 'text_2',
                    extra: {
                        cls: MY_URL + "_选集",
                        id: pd(data, 'a&&href')
                    }
                };
            });
            deleteItemByCls(MY_URL + "_选集");
            addItemBefore(MY_URL + "footer", cp);
            return "hiker://empty"
        }, tabs, lazy, id),
        col_type: 'flex_button',
        extra: {
            id: MY_URL + "_线路" + id
        }
    });
})

if (排序 == '#FF0000">倒序') {
    list.reverse()
}
list.forEach((data, id) => {
    d.push({
        title: pdfh(data, 'a&&Text'),
        url: pd(data, 'a&&href') + lazy,
        col_type: list.length > 3 ? 'text_4' : 'text_2',
        extra: {
            cls: MY_URL + "_选集",
            id: pd(data, 'a&&href')
        },
    });
})
d.push({
    col_type: "big_blank_block",
    extra: {
        id: MY_URL + "footer"
    }
});
setResult(d);

