const txt_name = "githubregistry";
const password = "hiker721";

function getRegistry() {
    try {
        let registry = post("https://a6.qikekeji.com/txt/data/detail/", {
            body: {
                txt_name: txt_name,
                password: password
            }
        });
        let rd = JSON.parse(registry).data;
        registry = JSON.parse(rd.txt_content)[0].content || "";
        return {
            content: JSON.parse(registry),
            vid: rd.v_id
        };
    } catch {
        return {
            content: []
        };
    }
}

function setRegistry(registry) {
    try {
        let res = post("https://a6.qikekeji.com/txt/data/save/", {
            body: {
                txt_name: txt_name,
                password: password,
                txt_content: JSON.stringify([{
                    "title": "注册表",
                    "content": JSON.stringify(registry.content)
                }]),
                v_id: registry.vid || ""
            }
        });
        res = JSON.parse(res);
        if (res.status === 1) {
            clearMyVar("developer");
            return true;
        }
        return false;
    } catch (e) {
        log(e.toString());
        return false
    }
}


function getDeveloper(renovate) {
    let developer = storage0.getMyVar("developer", "");
    if (!developer || renovate) {
        developer = getRegistry().content;
        storage0.putMyVar("developer", developer);
    }
    return developer;
}

function setDevInfo(uesrName, info) {
    let registry = getRegistry();
    let index = registry.content.findIndex(v => v.name === uesrName);
    registry.content.splice(index, 1, Object.assign(registry.content[index], info));
    return setRegistry(registry);
}

function addDev(uesr) {
    let registry = getRegistry();
    registry.content.push(uesr);
    return setRegistry(registry);
}

function deleteDev(uesrName) {
    let registry = getRegistry();
    let index = registry.content.findIndex(v => v.name === uesrName);
    registry.content.splice(index, 1);
    return setRegistry(registry);
}

function moveDev(uesrName, newIndex) {
    let registry = getRegistry();
    let index = registry.content.findIndex(v => v.name === uesrName);
    let info = registry.content.splice(index, 1)[0];
    registry.content.splice(newIndex, 0, info);
    return setRegistry(registry);
}
$.exports = {
    setRegistry: setRegistry,
    getRegistry: getRegistry,
    getDeveloper: getDeveloper,
    setDevInfo: setDevInfo,
    addDev: addDev,
    deleteDev: deleteDev,
    moveDev: moveDev
};

