const HOST = "api.github.com";
const basicUrl = "https://" + HOST;

function getContents(repo, path, error, token) {
    let headers = {
        Accept: "application/vnd.github.v3+json",
    };
    let guest_token = token || getItem('guest_token', '');
    if (guest_token) {
        headers.Authorization = "token " + guest_token;
    }
    let res = fetch(basicUrl + "/repos/" + repo + "/contents/" + (path || ""), {
        headers: headers
    });
    res = JSON.parse(res);
    if (res.message && error) {
        return error(res);
    } else if (res.message) {
        throw new Error(res.message);
    }
    return res;
}

function getRepoInfo(repo, token) {
    let login = JSON.parse(fetch(basicUrl + "/repos/" + repo, {
        headers: {
            Accept: "application/vnd.github.v3+json",
            Authorization: "token " + token
        }
    }));
    if (login.message) {
        throw new Error(login.message);
    }
    return login;
}

function createFile(repo, token, path, content, sha) {
    let upUrl = basicUrl + "/repos/" + repo + "/contents/" + path;
    try {
        if (sha === void 0) {
            sha = getContents(repo, path, void 0, token).sha;
        }
    } catch (e) {
        if (e.message != "Not Found") {
            throw e;
        }
    }
    const res = JSON.parse(fetch(upUrl, {
        body: {
            branch: 'main',
            message: 'upload',
            content: base64Encode(content || ""),
            path: path,
            sha: sha || ""
        },
        headers: {
            "Authorization": "token " + token,
            'Content-Type': 'application/json; charset=utf-8',
        },
        method: "PUT"
    }));
    if (res.message) {
        throw new Error(res.message);
    }
    return res;
}

function deleteFile(repo, token, path, sha) {
    let deleteUrl = basicUrl + "/repos/" + repo + "/contents/" + path;
    if (!sha) {
        sha = getContents(repo, path).sha;
    }
    let res = JSON.parse(fetch(deleteUrl, {
        headers: {
            Accept: "application/vnd.github.v3+json",
            Authorization: "token " + token
        },
        body: {
            sha: sha,
            message: "删除规则"
        },
        method: "DELETE",
        withStatusCode: true
    }));
    if (res.statusCode === 200) {
        java.lang.Thread.sleep(1650);
        return true;
    } else {
        res = JSON.parse(res.body);
        throw new Error(res.message);
    }

}

function changeTable(repo, token, func, path) {
    path = path || "manifest.json";
    let summary = getContents(repo, path, (e) => {
        if (e.message === "Not Found") {
            return {
                content: ""
            };
        } else {
            throw new Error("摘要表获取失败");
        }
    }, token);
    let table;
    if (summary.content) {
        table = JSON.parse(base64Decode(summary.content));
        if (!Array.isArray(table.rules)) {
            table.rules = [];
        }
    } else {
        table = {
            rules: []
        };
    }
    createFile(repo, token, path, JSON.stringify(func(table)), summary.sha || "");
}
$.exports = {
    getContents: getContents,
    getRepoInfo: getRepoInfo,
    createFile: createFile,
    deleteFile: deleteFile,
    changeTable: changeTable
}

const GithubAPI = $.require("GithubAPI?rule=Github仓库");
const dirEnum = $.require("directoryEnum.json?rule=Github仓库");
const utils = $.require("utils?rule=Github仓库");
$.exports = function(repo, token, MY_RULE, config) {
    let pass, path, isRule;
    let content='';
    if (Array.isArray(MY_RULE)) {
        path = dirEnum.SET + MY_RULE[0].group + ".json";
        //pass = "海阔视界首页合集规则【" + MY_RULE[0].group + "】￥home_rule_url￥";
        content=JSON.stringify(MY_RULE);
    }else if (typeof(MY_RULE)=='string') {
        let fileName;
        log(MY_RULE);
        if(/^(hiker|file):/.test(MY_RULE)){
            content=fetch(MY_RULE);
            fileName=MY_RULE.split('/').slice(-1)[0];
            fileName=utils.removeFileExtension(fileName);

        }else{
            return '暂只支持hiker|file开头的文件分享'
        }

        if(/\.html$/.test(MY_RULE)){
            path = dirEnum.HTML + fileName + ".html";
        }else if(/\.js$/.test(MY_RULE)){
            path = dirEnum.JS + fileName + ".js";
        }else if(/\.json$/.test(MY_RULE)){
            path = dirEnum.JSON + fileName + ".json";
        }else{
            path = dirEnum.TXT + fileName + ".txt";
        }
        //pass = "海阔视界首页合集规则【" + MY_RULE[0].group + "】￥home_rule_url￥";

    }else {
        isRule = true;
        path = dirEnum.RULE + MY_RULE.title + ".json";
        //pass = "海阔视界首页频道规则【" + MY_RULE.title + "】￥home_rule_url￥";
        content=JSON.stringify(MY_RULE);
    }

    try {
        if (isRule) {
            GithubAPI.changeTable(repo, token, table => {
                let newIndex = {
                    title: MY_RULE.title,
                    icon: MY_RULE.icon,
                    version: MY_RULE.version,
                    author: MY_RULE.author,
                    type: MY_RULE.type,
                    group: MY_RULE.group,
                    url: MY_RULE.url
                };
                let index = table.rules.findIndex(it => it.title == MY_RULE.title);
                if (index > -1) {
                    table.rules.splice(index, 1, newIndex);
                } else {
                    table.rules.push(newIndex);
                }
                return table;
            });
            java.lang.Thread.sleep(350);
        }
        GithubAPI.createFile(repo, token, path, content);
        return "上传成功";
    } catch (e) {
        log(e.toString());
        return "上传失败";
    }
}

