js:
function Color(text, color) {
    return text.toString().fontcolor(color);
}

function HtmlTag(tag, text) {
    return "<" + tag + ">" + text + "</" + tag + ">";
}

function Rich(text) {
    return "““””" + text;
}

function Small(text) {
    return HtmlTag("small", text);
}

function removeFileExtension(fileName) {
    var lastIndex = fileName.lastIndexOf(".");
    if (lastIndex === -1) { // 如果文件名没有扩展名，则返回原始文件名
        return fileName;
    }
    return fileName.substr(0, lastIndex);
}

function GetDirCn(ext){
    let name='';
    switch(ext){
        case 'RULE':
          name='小程序';
          break;
        case 'JS':
          name='网页插件';
          break;
        case 'SET':
          name='规则合集';
          break;
        case 'HTML':
          name='静态网页';
          break;
        case 'JSON':
          name='json文件';
          break;
        case 'TXT':
          name='文本文件';
          break;
        default:
          name='其他'
    }
    return name
}

$.exports = {
    Color: Color,
    HtmlTag: HtmlTag,
    Rich: Rich,
    Small: Small,
    GetDirCn:GetDirCn,
    removeFileExtension: removeFileExtension
}

