const GithubAPI = $.require("GithubAPI?rule=Github仓库");
const dirEnum = $.require("directoryEnum.json?rule=Github仓库");
const utils = $.require("utils");


addListener("onClose", () => {
    clearMyVar("typeview");
    clearMyVar("listCache");
    clearMyVar("indexCache");
});

addListener("onRefresh", () => {
    clearMyVar("listCache");
    clearMyVar("indexCache");
});
let cls = [];
let type = Object.keys(dirEnum);
let selType = getMyVar("typeview", type[0]);
for (let it of type) {
    let isType = it === selType;
    cls.push({
        title: isType ? "““" + utils.GetDirCn(it) + "””" : utils.GetDirCn(it),
        url: $("#noLoading#").lazyRule((it, isType) => {
            if (isType) {
                return "toast://已经是当前目录";
            }
            putMyVar("typeview", it);
            refreshPage(false);
            return "hiker://empty";
        }, it, isType),
        col_type: "scroll_button",
    });
}
cls.push({
    title: "正在加载...",
    url: "hiker://empty",
    col_type: "text_center_1",
    extra: {
        id: "Githubloading",
        lineVisible: false
    }
});
setPreResult(cls);
let listCache = storage0.getMyVar("listCache", {});
let indexCache = storage0.getMyVar("indexCache", {});
let d = [];


let indexData = [];
if (selType === "RULE") {
    indexData = [];
    if (!indexCache.rules) {
        try {
            let indexObj = GithubAPI.getContents(MY_PARAMS.repo, "manifest.json");
            if (indexObj.sha) {
                indexObj = JSON.parse(base64Decode(indexObj.content));
                indexData = indexObj.rules;
                storage0.putMyVar("indexCache", indexObj);
            }
        } catch (e) {
            log("摘要获取失败：" + e.toString());
        }
    } else {
        indexData = indexCache.rules;
    }
}

function getInfo(item) {
    let obj = {
        col_type: "icon_2_round",
    };
    if (selType === "RULE") {
        obj.title = utils.removeFileExtension(item.name);
        let indexContent = indexData.find(v => v.title == obj.title) || {};
        obj.pic_url = indexContent.icon;
        obj.url = "rule://" + base64Encode("￥home_rule_url￥" +getItem('raw_proxy','')+ item.download_url);
    } else if (selType === "SET") {
        obj.title = utils.removeFileExtension(item.name);
        obj.pic_url = "hiker://images/rule_type_read";
        obj.url = "rule://" + base64Encode("￥home_rule_url￥"+getItem('raw_proxy','') + item.download_url);
    }else if (["HTML","JSON","JS","TXT"].includes(selType)) {
        obj.title = item.name;
        obj.pic_url = "hiker://images/rule_type_read";
        obj.url = $('#noLoading#').lazyRule((item)=>{
            let rawUrl=getItem('raw_proxy','') + item.download_url;
            copy(rawUrl);
            return rawUrl
        },item)
    }

    return obj;
}

function setManage(obj, item) {
    obj.url = $(["删除", "复制分享链接", "导入"]).select((url, sha, name, path) => {
        if (input === "复制分享链接") {
            return "copy://海阔视界规则【" + name + "】￥home_rule_url￥" + url;
        } else if (input === "导入") {
            return "rule://" + base64Encode("￥home_rule_url￥" + url);
        } else {
            return $("确认从仓库删除该分享:" + name).confirm((path, sha) => {
                let user = storage0.getItem("user", "");
                showLoading("正在删除");
                const GithubAPI = $.require("GithubAPI?rule=Github仓库");
                try {
                    let res = GithubAPI.deleteFile(user.repo, user.token, path, sha);                    
                    if (res) {
                        java.lang.Thread.sleep(2650);
                        clearMyVar("listCache");
                        refreshPage(false);
                        hideLoading();
                        return "toast://删除成功,服务器删除会有一定延迟";
                    }
                    hideLoading();
                    return "toast://删除失败";
                } catch (e) {
                    hideLoading();
                    return "toast://删除失败:" + e.toString();
                }
            }, path, sha);
        }
    }, (getItem('raw_proxy','')+item.download_url), item.sha, item.name, item.path);
    return obj;
}
try {
    let list = [];
    if (listCache[selType] === void 0) {
        list = GithubAPI.getContents(MY_PARAMS.repo, dirEnum[selType], (e) => {
            if (["This repository is empty.", "Not Found"].includes(e.message)) {
                return [];
            } else {
                throw new Error(e.message);
            }
        });
        list = list.filter(v => v.type === "file");
        listCache[selType] = list || [];

        storage0.putMyVar("listCache", listCache);
    } else {
        list = listCache[selType];
    }
    if (!list.length) {
        d.push({
            title: "““””" + "仓库是空的哦".small().fontcolor("grey"),
            url: "hiker://empty",
            col_type: "text_center_1"
        });
    } else {
        if(MY_PARAMS.isDev){
            list.forEach(it => d.push(setManage(getInfo(it), it)));
        }else{
            list.forEach(it => d.push(getInfo(it)));
        }
    }
    setResult(d);
} catch (e) {
    setResult([{
        title: "““ERROR””",
        desc: e.toString(),
        url: "hiker://empty",
        col_type: "text_center_1"
    }]);
} finally {
    deleteItem("Githubloading");
}

