const HOST = "api.github.com";
const basicUrl = "https://" + HOST;

function getContents(repo, path, error, token) {
    let headers = {
        Accept: "application/vnd.github.v3+json",
    };
    let guest_token = token || getItem('guest_token', '');
    if (guest_token) {
        headers.Authorization = "token " + guest_token;
    }
    let res = fetch(basicUrl + "/repos/" + repo + "/contents/" + (path || ""), {
        headers: headers
    });
    res = JSON.parse(res);
    if (res.message && error) {
        return error(res);
    } else if (res.message) {
        throw new Error(res.message);
    }
    return res;
}

function getRepoInfo(repo, token) {
    let login = JSON.parse(fetch(basicUrl + "/repos/" + repo, {
        headers: {
            Accept: "application/vnd.github.v3+json",
            Authorization: "token " + token
        }
    }));
    if (login.message) {
        throw new Error(login.message);
    }
    return login;
}

function createFile(repo, token, path, content, sha) {
    let upUrl = basicUrl + "/repos/" + repo + "/contents/" + path;
    try {
        if (sha === void 0) {
            sha = getContents(repo, path, void 0, token).sha;
        }
    } catch (e) {
        if (e.message != "Not Found") {
            throw e;
        }
    }
    const res = JSON.parse(fetch(upUrl, {
        body: {
            branch: 'main',
            message: 'upload',
            content: base64Encode(content || ""),
            path: path,
            sha: sha || ""
        },
        headers: {
            "Authorization": "token " + token,
            'Content-Type': 'application/json; charset=utf-8',
        },
        method: "PUT"
    }));
    if (res.message) {
        throw new Error(res.message);
    }
    return res;
}

function deleteFile(repo, token, path, sha) {
    let deleteUrl = basicUrl + "/repos/" + repo + "/contents/" + path;
    if (!sha) {
        sha = getContents(repo, path).sha;
    }
    let res = JSON.parse(fetch(deleteUrl, {
        headers: {
            Accept: "application/vnd.github.v3+json",
            Authorization: "token " + token
        },
        body: {
            sha: sha,
            message: "删除规则"
        },
        method: "DELETE",
        withStatusCode: true
    }));
    if (res.statusCode === 200) {
        java.lang.Thread.sleep(1650);
        return true;
    } else {
        res = JSON.parse(res.body);
        throw new Error(res.message);
    }

}

function changeTable(repo, token, func, path) {
    path = path || "manifest.json";
    let summary = getContents(repo, path, (e) => {
        if (e.message === "Not Found") {
            return {
                content: ""
            };
        } else {
            throw new Error("摘要表获取失败");
        }
    }, token);
    let table;
    if (summary.content) {
        table = JSON.parse(base64Decode(summary.content));
        if (!Array.isArray(table.rules)) {
            table.rules = [];
        }
    } else {
        table = {
            rules: []
        };
    }
    createFile(repo, token, path, JSON.stringify(func(table)), summary.sha || "");
}
$.exports = {
    getContents: getContents,
    getRepoInfo: getRepoInfo,
    createFile: createFile,
    deleteFile: deleteFile,
    changeTable: changeTable
}"},{"col_type":"movie_3","name":"提交规则","path":"submit","rule":"const GithubAPI = $.require("GithubAPI?rule=Github仓库");
