                saveFile('JSON_' + Hash, JSON.stringify(AllJson), 0)
                refreshPage(false)
            }, k, Path, Hash)
        case 'Âà†Èô§È°πÁõÆ':
            return $(`Á°ÆÂÆöË¶ÅÂà†Èô§ÈîÆÂêç[${k}]ÁöÑÈ°πÁõÆÂêóÔºü`).confirm((k, Path, Hash) => {
