                if (input === '') input = Infinity
                else {
                    input = parseInt(input, 10)
                    if (isNaN(input)) return 'toast://ËØ∑Ê≠£Á°ÆËæìÂÖ•Êï∞Â≠óÂ∫èÂè∑'
				}
