"},{"col_type":"movie_3","name":"ÂÖ¨ÂÖ±ÂáΩÊï∞","path":"functions","rule":"function JSONRecur(json, path, value) {
    if (path.length == 1) {
        if (value === undefined) {
            return json[path[0]]
        } else if (typeof value == 'function') {
            json[path[0]] = value(json[path[0]])
        } else json[path[0]] = value
    } else {
        return JSONRecur(json[path[0]], path.slice(1), value)
    }
}

function typeConvert(value, type) {
    let vType = Object.prototype.toString.call(value).slice(8, -1)
    switch (type) {
        case 'Object':
            switch (vType) {
                case 'Object':
                    return value
                case 'Array':
                    let obj = {}
                    for (let v of value) {
                        if (v && Object.keys(v).length == 2
                            && 'key' in v && 'value' in v &&
                            !Object.hasOwn(obj, v.key)) {
                            obj[v.key] = v.value
                        } else break
                    }
                    if (Object.keys(obj).length < value.length) {
                        obj = Object.assign({}, value)
                        for (let k in obj) if (obj[k] === null) delete obj[k]
                    }
                    return obj
                case 'String':
                    try {
                        return typeConvert(JSON.parse(value), type)
                    } catch (e) { return {} };
                default:
                    return {}
            };
        case 'Array':
            switch (vType) {
                case 'Object':
                    let keys = Object.keys(value), length = 0
                    for (let k of keys)
                        if (/^\d+$/.test(k)) {
                            k = Number(k) + 1
                            if (length < k) length = k
                        } else {
                            length = 0
                            break
                        }
                    return length > 0
                        ? Array.from(Object.assign(value, { length: length }))
                        : keys.map((k) => ({ key: k, value: value[k] }))
                case 'Array':
                    return value
                case 'String':
                    try {
                        return typeConvert(JSON.parse(value), type)
                    } catch (e) { return [] };
                default:
                    return []
            };
        case 'String':
            switch (vType) {
                case 'Object':
                case 'Array':
                    return JSON.stringify(value)
                default:
                    return String(value)
            };
        case 'Number':
            return Number(value) || 0
        case 'Boolean':
            try {
                while (typeof value == 'string')
                    value = JSON.parse(value)
            } catch (e) {}
            return Boolean(value)
        case 'Null':
            return null
    }
}

