var d = [];

function formatDate(_date, _fmt) {
    let fmt = _fmt || "yyyy-MM-dd HH:mm:ss";
    const date = !isNaN(_date) ? new Date(_date * 1000) : new Date(_date);
    const o = {
        "M+": date.getMonth() + 1, //Êúà‰ªΩ 
        "d+": date.getDate(), //Êó• 
        "h+": date.getHours() % 12 == 0 ? 12 : date.getHours() % 12,
        "H+": date.getHours(), //Â∞èÊó∂ 
        "m+": date.getMinutes(), //ÂàÜ 
        "s+": date.getSeconds(), //Áßí 
        "q+": Math.floor((date.getMonth() + 3) / 3), //Â≠£Â∫¶ 
        "S": date.getMilliseconds() //ÊØ´Áßí 
    };
    if (/(y+)/.test(fmt)) {
        fmt = fmt.replace(RegExp.$1, (date.getFullYear() + "").substr(4 - RegExp.$1.length));
    }
    for (let k in o) {
        if (new RegExp("(" + k + ")").test(fmt)) {
            fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
        }
    }
    return fmt;
};

function formatSize(size) {
    if (!size) {
        return '';
    }
    const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    let i = 0;
    while (size >= 1024) {
        size /= 1024;
        i++;
    }
    size = i ? Number(size.toFixed(2)) : size;
    return `${size} ${units[i]}`;
}
