function open(fileId, defdriveid) {
    try {
        let json = "";
        let number = 0;
        do {
            json = fetch('https://open.aliyundrive.com/adrive/v1.0/openFile/getDownloadUrl', {
                headers: {
                    'User-Agent': PC_UA,
                    'Referer': 'https://www.aliyundrive.com/',
                    'authorization': OpenToken.AccessTokenOpen
                    //getAccessTokenOpen

                },
                body: {
                    'file_id': fileId,
                    'drive_id': defdriveid
                },
                method: 'POST'
            });
            //log('open\n' + json);
            if (json.includes('"url":"http')) {
                break;
            }
            if (json.includes("TooManyRequests")) {
                //deletee(fileId);
                toast('Too Many Requests');
                log('TooManyRequests');
                //return 'toast://ËØ∑Ê±ÇÂ§™È¢ëÁπÅ';
            }
            if (getMyVar('redirect', '0') == '0') {
                oauthRequest();
            } else {
                refreshOpenToken();
            }

            number++
        } while (number < 2);
        return JSON.parse(json).url;
    } catch (e) {
        return 'hiker://empty'
    }
};

function getQuality(taskList) {
    let orders = ["UHD", "QHD", "FHD", "HD", "SD", "LD"];
    for (let or of orders) {
        for (let i = 0; i < taskList.length; i++) {
            let task = taskList[i];
            if (task.template_id == or) {
                return task.url;
            }
        }
    }
    return taskList[0].url;
};

