                    showLoading("正在获取...");
                    dm = $.require('hiker://page/dmFun?rule=dm盒子').dmRoute(vipUrl);
                    dmPath = getPath(getVar('dm_share')).slice(7);
                    var {copyFile,moveFile} = $.require("hiker://page/fileAPI?rule=dm盒子");
                    dmSettings = JSON.parse(request('hiker://files/dm盒子/settings.json'));
                    saveLocal=dmSettings.saveLocal;
                    doubleWrite=dmSettings.doubleWrite;
                    if (saveLocal) {
                        destinationDir='/storage/emulated/0/Download/海阔视界/danmu/';
                        if (doubleWrite) {
                            copyFile(dmPath,destinationDir);
                        }else{
                            moveFile(dmPath,destinationDir);
                        }
                    }
                    hideLoading();
                    if (dm === false) {
                        return "toast://貌似下载出错了呜呜呜";
                    } else {
                        return "toast://下载成功，快去观影吧，。";
                    }
                } else if (input == '勾选/取消') {// 勾选
                    function removeDuplicatesAndArrange(array, element) {
                        var index = array.indexOf(element);
                        var result = [];
                        if (index !== -1) {
                            result = array.filter(function (value, i) {
                                return i !== index;
                            });
                        } else {
                            result = array.concat(element);
                        }
                        return result;
                    }
                    let checkArr = storage0.getMyVar('checkArr', []);
                    olength = checkArr.length;
                    checkArr = removeDuplicatesAndArrange(checkArr, vipUrl);
                    alength = checkArr.length;
                    storage0.putMyVar('checkArr', checkArr);
                    if (alength > olength) {
                        return "toast://加入下载列表成功";
                    } else {
                        return "toast://从下载列表中移除";
                    }
                } else if (input == '下载勾选') {// 批量下载
                    return $('', "请输入开始集数").input(() => {
                        var javaImport = new JavaImporter(java.io, Packages.android.util);
                        function renameFile(filePath, newFileName) {
                            with (javaImport) {
                                var file = new File(filePath);
                                var newFilePath = new File(file.getParent(), newFileName);
                                if (file.renameTo(newFilePath)) {
                                    log("文件重命名成功！");
                                } else {
                                    log("文件重命名失败！");
                                }
                            }
                        }
                        let ep = Number(input);
                        let checkArr = storage0.getMyVar('checkArr', []);
                        log('批量下载列表：'+JSON.stringify(checkArr));
                        checkArr.forEach(vipUrl => {
                            showLoading("下载进度：第" + ep + "集");
                            dm = $.require('hiker://page/dmFun?rule=dm盒子').dmRoute(vipUrl);
                            hideLoading();
                            if (dm === false) {
                                log('第' + ep + '集下载出错');
                            }
                            dmPath = getPath(getVar('dm_share'));
                            newFileName = dmPath.substring(dmPath.lastIndexOf("/") + 1).replace(/(\.[^.]+)$/, '_' + ep + "$1");
                            localPath = android.net.Uri.parse(dmPath).getPath();
                            renameFile(localPath, newFileName);
                            var {copyFile,moveFile} = $.require("hiker://page/fileAPI?rule=dm盒子");
                            dmSettings = JSON.parse(request('hiker://files/dm盒子/settings.json'));
                            saveLocal=dmSettings.saveLocal;
                            doubleWrite=dmSettings.doubleWrite;
                            newPath=getPath('hiker://files/cache/danmu/').slice(7)+newFileName;
                            if (saveLocal) {
                                destinationDir='/storage/emulated/0/Download/海阔视界/danmu/';
                                if (doubleWrite) {
                                    copyFile(newPath,destinationDir);
                                }else{
                                    moveFile(newPath,destinationDir);
                                }
                            }
                            ep++;
                        });
                        clearMyVar('checkArr');
                        return "toast://已全部下载，快去观影吧，。";
                    })
                } else {
                    return 'hiker://page/setting?rule=dm盒子'
                }
            }, vipUrl, jiexiArr)
            break;
    }
