$.exports.saveLocal = () => {
    const base64ToHex = (base64Str) => {
        // 删除前缀并解码Base64字符串
        var binaryStr = window0.atob(base64Str.split(',')[1]);

        // 转换为16进制字符串
        var hexStr = '';
        for (var i = 0; i < binaryStr.length; i++) {
            var hex = binaryStr.charCodeAt(i).toString(16);
            hexStr += (hex.length === 2 ? hex : '0' + hex);
        }
        return hexStr;
    };
    showLoading("图标初始化中...");
    var tubiao = {};
    try {
        // 剪贴板线路
        html = request('https://netcut.cn/p/a1fcf0d3b0a27f13');
        data = html.match(/{.*note_content.*updated_at.*}/)[0];
        eval('tubiao =' + JSON.parse(data).note_content);
    } catch (e) {
        log('剪贴板出错,切换托管线路');
        route = [
            'https://gitcode.net/qq_23847819/hiker/-/raw/master/tubiao.json',
            'https://gitea.com/cy/hiker/raw/commit/be2160c6a01db873d9f9681ba7e86eebfa118d86/tubiao.json',
            'https://raw.codehub.cn/p/hiker/d/tubiao/git/raw/master/tubiao.json?token=GGdlIuVGqY3b3LydIowrksbUBCfMyx2fF31WUZeo4m',
            'https://gitee.com/mxxl-cy/hiker/raw/master/tubiao.json'
        ];
        for (let i = 0; i < route.length; i++) {
            try {
                eval('tubiao =' + request(route[i]));
                if (tubiao["五大"] && tubiao["哔哩"]) {
                    break;
                } else {
                    log('线路：' + tubiao[i] + '被墙了...');
                }
            } catch (e) {
                log('线路：' + tubiao[i] + '出错了...');
            }
        }
    }
    try {
        var wuda = tubiao["五大"];
        var bili = tubiao["哔哩"];
        var index = 0;
        for (let item in wuda) {
            writeHexFile(`hiker://files/rules/cy/tubiao/${item}.svg`, base64ToHex(wuda[item]));
            log(`初始化第${index++}个图标:${item}...`);
        }
        for (let item in bili) {
            writeHexFile(`hiker://files/rules/cy/bili_pic/${item}.png`, base64ToHex(bili[item]));
            log(`初始化第${index++}个图标:${item}...`);
        }
        showLoading('图标初始化完成：共计' + index + '个图标');
        hideLoading();
    } catch (e) {
        showLoading('图标初始化失败');
        log('图标初始化失败:' + e);
        hideLoading();
    }

};

