let d = [];
var javaImport = new JavaImporter(java.io, Packages.android.util);
// 读取目录下的所有文件
function listFilesInDir(dirPath) {
    with (javaImport) {
        var files = [];
        var localPath = android.net.Uri.parse(dirPath).getPath();
        var dir = new File(localPath);
        if (dir.isDirectory()) {
            var fileArr = dir.listFiles();
            for (let i = 0; i < fileArr.length; i++) {
                var file = fileArr[i];
                if (file.isFile()) {
                    files.push({
                        name: file.getName(),
                        path: file.getAbsolutePath(),
                        size: file.length()
                    });
                }
            }
        }
        return files;
    }
}
// 路径选择
select=getMyVar('filePath','0');
d.push({
    title:select=='0'?'““””<b><span style="color: red">内部文件</span></b>':'内部文件',
    url:$('#noLoading#').lazyRule((path,name) => {
        putMyVar('filePath','0');
        refreshPage(true);
        return 'hiker://empty';
    }),
    col_type:'scroll_button'
},{
    title:select=='1'?'““””<b><span style="color: red">外部文件</span></b>':'外部文件',
    url:$('#noLoading#').lazyRule((path,name) => {
        putMyVar('filePath','1');
        refreshPage(true);
        return 'hiker://empty';
    }),
    col_type:'scroll_button'
})
// 调用 listFilesInDir 函数，获取所有文件信息
if (select=='0') {
    var path = getPath('hiker://files/cache/danmu/');
} else {
    var path = getPath('/storage/emulated/0/Download/海阔视界/danmu/');
}
var files = listFilesInDir(path);
d.push({
    title: '清空',
    url: $("你确定嘛").confirm((files) => {
        if (files.length==0) {
            return 'toast://没有垃圾，。'
        }
        showLoading('删除中...');
        files.forEach(el=>deleteFile(el.path));
        hideLoading();
        refreshPage(true);
        return 'toast://清空完毕'
    },files),
    desc:'内部文件三天内自动删除|外部文件请定期删除',
    col_type: 'text_center_1'
})
d.push({
    col_type: 'line_blank'
})
if(files.length==0){
    d.push({
        title:'““””<font color="red">暂时没有产生垃圾！</font>',
        url:'toast://去制造垃圾啊，。',
        col_type:'text_center_1'
    });
}else{
    files.forEach(el => {
        with (javaImport) {
            avatar = ((new File(el.path)).isDirectory()) ? 'hiker://files/icon/文件类型/文件夹.svg' : 'hiker://files/icon/文件类型/文件.svg';
        }
        d.push({
            title: el.name,
            url: $('#noLoading#').lazyRule((path,name) => {
                return $(['查阅', '分享', '删除'], 3,name).select((path) => {
                    if (input == '查阅') {
                        return 'openFile://' + path.replace('/storage/emulated/0/Android/data/com.example.hikerview/files/Documents/', 'hiker://files/');
                    } else if (input == '分享') {
                        return 'share://' + path;
                    } else {
                        deleteFile(path);
                        refreshPage(true);
                        return 'toast://删除成功';
                    }
                }, path)
            }, el.path,el.name),
            img: avatar,
            col_type: 'avatar'
        })
    });
}
setResult(d);
