        function getMyHeaders(urls){
            return urls.map((it) => {
                if (!/;/.test(it) || it.split(';').length < 2) {
                    return {}
                } else {
                    let head = it.split(';')[1];
                    if (!/@/.test(head) || !/{|}/.test(head)) {
                        return {}
                    }
                    head = head.split("{")[1].split("}")[0];
                    //head = head.replace(/{|}/g,'');
                    head = head.split('&&');
                    let obj = {};
                    for (let i in head) {
                        let key = head[i].split('@')[0];
                        let value = head[i].split('@')[1].replace(/;/, '；；');
                        obj[key] = value;
                    }
                    return obj
