$.exports.checkFileExists = function (filePath) {
    var javaImport = new JavaImporter(java.io);
    with (javaImport) {
        var file = new File(filePath);
        return file.exists();
    }
}
$.exports.createDirectory = function (directoryPath) {
    var javaImport = new JavaImporter(java.io);
    with (javaImport) {
        var directory = new File(directoryPath);
        if (!directory.exists()) {
            if (!directory.mkdirs()) {
                console.log("目标文件夹创建失败：" + directoryPath);
            }
        }
    }
}
$.exports.renameFile = function (filePath, newFileName) {
    var javaImport = new JavaImporter(java.io);
    with (javaImport) {
        var file = new File(filePath);
        var parentDir = file.getParent();
        var newFilePath = parentDir + "/" + newFileName;

        if (!$.require("hiker://page/fileAPI?rule=dm盒子").checkFileExists(filePath)) {
            console.log("源文件不存在，请检查路径：" + filePath);
            return;
        }

        try {
            if (!file.renameTo(new File(newFilePath))) {
                console.log("文件重命名失败！");
            }
        } catch (e) {
            console.log("文件重命名失败！");
            console.log("失败原因：" + e.message);
        }
    }
}
$.exports.copyFile = function (sourcePath, destinationDir) {
    var javaImport = new JavaImporter(java.io);
    with (javaImport) {
        var file = new File(sourcePath);
        var destDir = new File(destinationDir);

        if (!$.require("hiker://page/fileAPI?rule=dm盒子").checkFileExists(sourcePath)) {
            console.log("源文件不存在，请检查路径：" + sourcePath);
            return;
        }

        try {
            if (!destDir.exists()) {
                console.log("目标文件夹不存在，将创建文件夹：" + destDir);
                $.require("hiker://page/fileAPI?rule=dm盒子").createDirectory(destDir.getPath());
            }

            var destFilePath = destinationDir + "/" + file.getName();
            var input = new FileInputStream(file);
            var output = new FileOutputStream(destFilePath);
            var buffer = java.lang.reflect.Array.newInstance(java.lang.Byte.TYPE, 1024);
            var length;
            while ((length = input.read(buffer)) > 0) {
                output.write(buffer, 0, length);
            }
            input.close();
            output.close();

            console.log("文件拷贝成功！");
        } catch (e) {
            console.log("文件拷贝失败！");
            console.log("失败原因：" + e.message);
        }
    }
}
$.exports.moveFile = function (sourcePath, destinationDir) {
    var javaImport = new JavaImporter(java.io);
    with (javaImport) {
        var file = new File(sourcePath);
        var destDir = new File(destinationDir);

        if (!$.require("hiker://page/fileAPI?rule=dm盒子").checkFileExists(sourcePath)) {
            console.log("源文件不存在，请检查路径：" + sourcePath);
            return;
        }

        try {
            if (!destDir.exists()) {
                console.log("目标文件夹不存在，将创建文件夹：" + destDir);
                $.require("hiker://page/fileAPI?rule=dm盒子").createDirectory(destDir.getPath());
            }

            var destFilePath = destinationDir + "/" + file.getName();
            if (!file.renameTo(new File(destFilePath))) {
                console.log("文件移动失败！");
            }
        } catch (e) {
            console.log("文件移动失败！");
            console.log("失败原因：" + e.message);
        }
    }
}
