//log((parseInt(timeToken) - parseInt(new Date().getTime() / 1000)).toString());
function accessTk() {
    if (reaccessTk == '' || (parseInt(timeToken) - parseInt(new Date().getTime() / 1000)).toString() <= 600) {
        log('Âà∑Êñ∞reaccessTk');
        let ntoken = JSON.parse(fetch('hiker://files/rules/icy/icy-ali-token.json', {}));
        var reftoken = ntoken[0].refresh_token;
        let refresh = fetch('https://auth.aliyundrive.com/v2/account/token', {
            headers: {
                'User-Agent': PC_UA,
                'Content-Type': 'application/json',
                'Referer': 'https://www.aliyundrive.com/'
            },
            body: '{"refresh_token":"' + reftoken + '","grant_type":"refresh_token"}',
            method: 'POST'
        });
        ntoken[0] = JSON.parse(refresh);
        if (!fileExist("hiker://files/rules/icy/icy-ali-token.json") || refresh.length > 300) {
            writeFile("hiker://files/rules/icy/icy-ali-token.json", JSON.stringify(ntoken));
        }
        putMyVar('timeToken', (parseInt(JSON.parse(refresh).expires_in) + parseInt(new Date().getTime() / 1000)).toString());
        defdriveid = JSON.parse(refresh).default_drive_id;
        putMyVar('defdriveid', defdriveid);
        reaccessTk = JSON.parse(refresh).token_type + ' ' + JSON.parse(refresh).access_token;
        putMyVar("aliaccessTk", reaccessTk);

    }
};
function resourceID() {
    let userresinfo = JSON.parse(fetch("https://user.aliyundrive.com/v2/user/get", {
        headers: {
            'content-type': 'application/json',
            'User-Agent': PC_UA,
            "authorization": reaccessTk,
            'Referer': 'https://www.aliyundrive.com/'
        },
        body: {},
        method: 'POST'
    }));
    if (userresinfo.resource_drive_id) {
        resdriveid = userresinfo.resource_drive_id;
        putMyVar('resdriveid', resdriveid);
    }
}
