function getsub(zmfid, zmext) {
    try {
        var json = fetch('https://api.aliyundrive.com/v2/file/get_share_link_download_url', {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': reaccessTk,
                'X-Share-Token': sharetoken
            },
            body: '{"share_id":"' + shareId + '","file_id":"' + zmfid + '"}',
            method: 'POST'
        });
        //log(json);
        let zimul = JSON.parse(json).download_url;
        downloadFile(zimul, 'hiker://files/download/subtitling.' + zmext, {
            'Referer': 'https://www.aliyundrive.com/'
        });
        return getPath('hiker://files/download/subtitling.' + zmext)
    } catch (e) {
        return "";
    }
}

