function bytesToSize(size) {
    if (size < 0.1 * 1024) {
        //å°äº0.1KBï¼Œåˆ™è½¬åŒ–æˆB
        size = size.toFixed(2) + "B";
    } else if (size < 0.1 * 1024 * 1024) {
        // å°äº0.1MBï¼Œåˆ™è½¬åŒ–æˆKB
        size = (size / 1024).toFixed(2) + "KB";
    } else if (size < 0.1 * 1024 * 1024 * 1024) {
        // å°äº0.1GBï¼Œåˆ™è½¬åŒ–æˆMB
        size = (size / (1024 * 1024)).toFixed(2) + "MB";
    } else {
        // å…¶ä»–è½¬åŒ–æˆGB
        size = (size / (1024 * 1024 * 1024)).toFixed(2) + "GB";
    }
    // è½¬æˆå­—ç¬¦ä¸²
    let sizeStr = size + "",
        // è·å–å°æ•°ç‚¹å¤„çš„ç´¢å¼•
        index = sizeStr.indexOf("."),
        // è·å–å°æ•°ç‚¹åä¸¤ä½çš„å€¼
        dou = sizeStr.substr(index + 1, 2);
    // åˆ¤æ–­åä¸¤ä½æ˜¯å¦ä¸º00ï¼Œå¦‚æœæ˜¯åˆ™åˆ é™¤00
    if (dou === "00") return sizeStr.substring(0, index) + sizeStr.substr(index + 3, 2);
    return size;
}
