function formatDate(_date, fmt) {
    let date = !isNaN(_date) ? new Date(_date * 1000) : new Date(_date);
    return $.dateFormat(date, fmt || "yyyy-MM-dd HH:mm:ss")
}

function formatSize(size) {
    if (!size) {
        return '';
    }
    const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
    let i = 0;
    while (size >= 1024) {
        size /= 1024;
        i++;
    }
    size = i ? Number(size.toFixed(2)) : size;
    return `${size} ${units[i]}`;
}

