function aliM3u8Proxy(fileId, shareId, sharePwd, shareToken, aliToken, title) {
    function getAliPlayUrl(fileId, line) {
        let aliurl;
        try {
            const AliAccessApi = $.require("hiker://page/aliPlayApi?rule=" + title);
            let aliAccessApi = AliAccessApi.createDefault(shareId, sharePwd, shareToken, aliToken, null, title);
            let playUrlList = aliAccessApi.getShareVideoPreview(fileId) || [];
            playUrlList.forEach((item) => {
