function postApi(api, body, headers, againTry, tryFre) {
    let result;
    try {
        result = request(api.startsWith("http") ? api : 'https://api.aliyundrive.com' + api, {
            headers: Object.assign({
                'Content-Type': 'application/json',
            }, headers),
            body: JSON.stringify(body),
            method: 'POST'
        });
        result = JSON.parse(result);
        if (result.code) {
            let msg = result.display_message || result.message;
            let error = new Error(api + "=>" + result.code + ":" + msg);
            error.code = result.code;
            error.judgeMsg = msg
            throw error;
        }
        let rbody = result && result.responses && result.responses[0] && result.responses[0].body || {};
        if (rbody.code) {
            let msg = rbody.display_message || rbody.message;
            let error = new Error(api + "=>" + rbody.code + ":" + msg);
            error.code = rbody.code;
            error.judgeMsg = msg;
            throw error;
        }
    } catch (e) {
        tryFre = tryFre === undefined ? 1 : tryFre;
        if (againTry && tryFre > 0) {
            if (typeof againTry === "function") {
                let newData = againTry(e, result, body, headers, tryFre);
                if (newData) {
                    Object.assign(body, newData.body);
                    Object.assign(headers, newData.headers);
                }
            }
            return postApi(api, body, headers, againTry, tryFre - 1);
        }
        throw e;
    }
    return result;
}

