            "auto_rename": true,
            "to_parent_file_id": "root",
            "to_drive_id": this.defDriveId
        }, "0", "/file/copy");
        let body = result && result.responses && result.responses[0] && result.responses[0].body || {};
        if (!body.file_id && !retry) {
            this.waitSleep(20).getShareToken();
            this.waitSleep(20).refreshAccessToken();
            return this.waitSleep(20).copyFileToCloudFile(fileId, true);
        } else if (!body.file_id) {
            let e = new Error("æ–‡ä»¶å¤åˆ¶å¤±è´¥:\n" + JSON.stringify(result));
            e.code = body.code;
            throw e;
        }
        return result.responses[0].body.file_id;
