        }, 2);
        if (!fileInfo.url || fileInfo.code) throw new Error("ä¸‹è½½é“¾æ¥è·å–å¤±è´¥\n" + fileInfo.code + ":" + fileInfo.message);
        return fileInfo.url;
    },
    deleteeCloudFile(cloudFileId) {
        let result = this._cloudFileOperate({
            "drive_id": this.defDriveId,
            "file_id": cloudFileId
        }, cloudFileId, "/file/delete");
        return result;
    },
    getVideoTemPlayUrl(fileId) {
        let cloudFileId = this.copyFileToCloudFile(fileId);
        this.waitSleep(20);
        let playUrl = this.openCloudFileToDownUrl(cloudFileId);
        this.waitSleep(50);
        try {
            this.deleteeCloudFile(cloudFileId);
        } catch (e) {
            log("#æ–‡ä»¶åˆ é™¤å¤±è´¥#\né”™è¯¯ä¿¡æ¯>" + e.toString() + "\né”™è¯¯è¡Œ>" + e.lineNumber);
        }
