    },
    getFileToDownUrl(fileId) {
        let downloadInfo = postApi("/v2/file/get_share_link_download_url", {
            "expire_sec": 600,
            "file_id": fileId,
            "share_id": this.shareId
        }, {
            "X-Share-Token": this.shareToken,
            "authorization": this.aliTokens.access_token
        }, (e) => {
            this.trySolve(e);
            return {
                headers: {
                    "X-Share-Token": this.shareToken,
                    "authorization": this.aliTokens.access_token
                }
            };
        });
        return downloadInfo.download_url;
    },
    getSign() {
        if (!this.aliTokens.device_id) {
            this.waitSleep(20).refreshAccessToken();
        }
        let calculateNewSign = () => {
            let sign = justTestSign("5dde4e1bdf9e4966b387ba58f4b3fdc3", this.aliTokens.device_id, this.aliTokens.user_id).split("##");
            return {
                signature: sign.at(0),
                pubKey: sign.at(-1)
            };
        };
        let newSign = calculateNewSign();
        let result = postApi('/users/v1/users/device/create_session', {
            "deviceName": "Edgeæµè§ˆå™¨",
            "modelName": "Windowsç½‘é¡µç‰ˆ",
            "pubKey": newSign.pubKey,
        }, {
            'User-Agent': PC_UA,
            'Referer': 'https://www.aliyundrive.com',
            'Authorization': this.reaccessTk,
            'X-Share-Token': this.shareToken,
            'x-device-id': this.aliTokens.device_id,
            'x-signature': newSign.signature
        }, (e) => {
            if (e.message === "x-device-id empty") {
                this.waitSleep(20).refreshAccessToken();
            }
            this.trySolve(e);
            this.waitSleep(50);
            newSign = calculateNewSign();
            return {
                body: {
                    "pubKey": newSign.pubKey
                },
                headers: {
                    'Authorization': this.reaccessTk,
                    'X-Share-Token': this.shareToken,
                    'x-device-id': this.aliTokens.device_id,
                    'x-signature': newSign.signature
                }
            }
        }, 3);
        if (result.success) {
            return newSign;
        } else {
            throw new Error("Signè·å–å¤±è´¥ï¼Œè¯·é‡è¯•ã€‚\ncode:" + (result.code || "") + "\nmessage:" + (result.message || ""));
        }
    },
    getShareVideoPreview(fileId) {
        let sign = this.getSign();
        let json = postApi('https://api.aliyundrive.com/v2/file/get_share_link_video_preview_play_info',{
            "category": "live_transcoding",
