AliAccessApi.createDefault = function(shareId, sharePwd, shareToken, aliToken, openToken, MY_TITLE) {
    const {
        openTokensFilepath,
        aliTokensFilepath
    } = $.require("tokenPath" + (MY_TITLE ? "?rule=" + MY_TITLE : ""));

    function saveAliToken(result, api) {
        writeFile(aliTokensFilepath, JSON.stringify(api.aliTokens));
    }

    function saveOpenToken(result, api) {
        writeFile(openTokensFilepath, JSON.stringify(api.openTokens));
    }

    function putShareToken(result) {
        if (MY_TITLE) {
            shareToken = putVar(MY_TITLE + "@" + "aliShareToken", result);
        } else {
            shareToken = putMyVar("aliShareToken", result);
        }
    }
    if (!openToken && openToken !== null && fileExist(openTokensFilepath)) {
        openToken = $.require(openTokensFilepath);
    }
    if (!aliToken && fileExist(aliTokensFilepath)) {
        aliToken = $.require(aliTokensFilepath);
    }
    if (MY_TITLE) {
        shareToken = getVar(MY_TITLE + "@" + "aliShareToken", shareToken);
    } else {
        shareToken = getMyVar("aliShareToken", shareToken);
    }
    return new AliAccessApi(shareId, sharePwd, shareToken, aliToken, openToken, putShareToken, saveAliToken, saveOpenToken);
}
