                }
            });

            let arr = pdfa(html, "main&&.card");
            //log(arr);
            let d = [];
            for (let i = 0; i < arr.length; i++) {
                let it = arr[i];
                let u = pdfh(it, ".card-link,1&&onclick").match(/https:\/\/(www\.aliyundrive\.com\/s|alywp\.net)\/\w*/g) || [];
                u = u.length > 0 ? u[0] : url;
                d.push({
                    title: pdfh(it, ".card-title&&Text"),
                    pic_url: pd(it, "img&&src", url),
                    url: u,
                    desc: pdfh(it, ".card-text,-1&&Text") + "\n" + u,
