function getPageSource(key) {
    let src = request("hiker://page/" + key + "?rule=模板·Q");
    src = JSON.parse(src).rule;
    return src;
}
const sourceMap = $.require("hiker://page/Mapping?rule=模板·Q");

function autoPage(htmlsrc, type) {
    let pageRules = [],
        htmlSource = htmlsrc,
        pageType,
        mMapping;
    if (type !== undefined) {
        pageType = type;
    } else {
        pageType = MY_TYPE;
    }
    mMapping = sourceMap[pageType];
    if (mMapping === undefined || !Array.isArray(mMapping)) {
        throw new Error("不存在" + pageType + "类型的模板");
    }
    for (let item of mMapping) {
        if (item.allKey) {
            if (item.keys.every(key => htmlSource.includes(key))) {
                pageRules.push(item);
            }
        } else {
            if (item.keys.some(key => htmlSource.includes(key))) {
                pageRules.push(item);
            }
        }
    }
    if(pageRules.length == 0) {
        log("暂无合适的模板");
    }
    for (let pageRule of pageRules) {
        log("正在匹配：" + pageRule.url);
        try {
            eval.call(null, ";(function(){" + getPageSource(pageRule.url) + "})()");
            log("匹配模板成功，正在结束匹配进程");
            break;
        } catch (e) {
            log("匹配模板错误");
            log(e.toString())
            log("正在匹配下一个模板......")
            if (pageRule.url == pageRules[pageRules.length - 1].url) {
                log("已匹配到最后一个模板，暂无合适的模板");
                setResult([]);
            }
        }
    }

}
$.exports.autoPage = autoPage;

