if (!config.依赖) {
    //远程依赖索引文件代理地址列表
    let requirelist = [{ url: 'https://ghproxy.net/https://raw.githubusercontent.com/', sort: 0 }, { url: 'https://ghproxy.com/https://raw.githubusercontent.com/', sort: 0 }, { url: 'https://cdn.staticaly.com/gh/', sort: 0 }, { url: 'https://github.jevons.vip/https://raw.githubusercontent.com/', sort: 0 }, { url: 'https://raw.githubusercontent.com/', sort: 0 }]
    let requirefile = "hiker://files/rules/Src/require.json";
    if (fetch(requirefile)) {
        try {
            eval("requirelist=" + fetch(requirefile) + ";");
        } catch (e) { }
    }
    requirelist.sort(function (x, y) {
        if (x.sort < y.sort) {
            return -1;
        } else if (x.sort > y.sort) {
            return 1;
        } else {
            return 0;
        }
    });
    for (let i = 0; i < requirelist.length; i++) {
        try { //远程依赖索引
            require(requirelist[i].url + 'src48597962/hk/Ju/require.js', { timeout: 2000 });
            if (relyfile) {
                break;
            }
        } catch (e) {
            requirelist[i].sort++;
            log(e.message);
        }
    }
    writeFile(requirefile, JSON.stringify(requirelist));
    initConfig({
        依赖: relyfile
    });
    let cfgfile = "hiker://files/rules/Src/Ju/config.json";
    if (fileExist(cfgfile)) {
        try {
            eval("let Juconfig=" + fetch(cfgfile) + ";");
            Juconfig["依赖"] = relyfile;
            writeFile(cfgfile, JSON.stringify(Juconfig));
        } catch (e) { }
    }
}
