function uprulefile() {
    try {
        let jiasu = 'https://github.moeyy.xyz/';
        var ruletxt = fetch(jiasu + 'https://raw.githubusercontent.com/xyq254245/HikerRule/main/ZYWCJ.txt', {});
        var rulejs = fetch(jiasu + 'https://raw.githubusercontent.com/xyq254245/HikerRule/main/zywcj.js', {});
    } catch (e) {
        rulejs = "";
        ruletxt = "";
    }
    if (rulejs.search(/lazyRule/) != -1) {
        writeFile("hiker://files/rules/xyq/zywcj.js", rulejs);
        writeFile("hiker://files/rules/xyq/ZYWCJ.txt", ruletxt);
    } else {
        setError('规则文件获取失败，可能网络有问题。')
    };
};

//检测是否有规则与JS文件
if (fileExist("hiker://files/rules/xyq/zywcj.js") && fileExist("hiker://files/rules/xyq/ZYWCJ.txt")) {
    //执行预处理
    var lac = fetch('hiker://files/rules/xyq/zywcj.js');
    if(lac.includes('codeberg')){
        writeFile("hiker://files/rules/xyq/zywcj.js", fetch('https://github.moeyy.xyz/https://raw.githubusercontent.com/xyq254245/HikerRule/main/zywcj.js', {}));
    }
    if (lac.search(/lazyRule/) != -1) {
        eval(lac);
        if (filter(base64Decode('VklQ'))) {
            zywpre();
        } else {
            uprulefile();
        };
    } else {
        //如果本地文件不对将尝试自修复
        uprulefile();
        eval(fetch('hiker://files/rules/xyq/zywcj.js'));
        zywpre();
    };
} else {
    uprulefile();
    //执行预处理
    eval(fetch('hiker://files/rules/xyq/zywcj.js'));
    zywpre();
};
