var d = [];
try {
    addListener('onClose', () => {
        clearVar('nameArray');
        clearVar('urlArray');
        clearVar('switch');
    })
    addListener('onRefresh', () => {
        setFile = readFile('hiker://files/rules/cy/MyParse.json');
        eval('configArray=' + setFile);
        clearVar('delNum');
        clearVar('delArray');
        clearVar('shareNum');
        clearVar('shareArray');
        for (let i = 0; i < configArray.length; i++) {
            clearVar('isChoose' + i);
        }
    })
    if (!fileExist('hiker://files/rules/cy/MyParse.json')) {
        configArray = [];
        saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
    }
    if (!fileExist('hiker://files/rules/cy/MyParse_selection.json')) {
        tagsArray = ['qq', 'mgtv', 'bili', 'qiyi', 'youku'];
        saveFile('hiker://files/rules/cy/MyParse_selection.json', JSON.stringify(tagsArray));
    }
    if (!fileExist('hiker://files/rules/cy/dmSwitch.txt')) {
        saveFile('hiker://files/rules/cy/dmSwitch.txt', '开');
    }
    if (!fileExist('hiker://files/dm盒子/settings.json')) {
        orgSettings = JSON.stringify({
            fontSize: 25,
            dmFrom: 'par'
        });
        saveFile('hiker://files/dm盒子/settings.json', orgSettings);
    }
    d.push({
        title: '‘‘’’<big><span style="color:#6EB897">添 加 解 析</span></big>',
        url: $('#noLoading#').lazyRule(() => {
            if (getVar('switch', '关') == '开') {
                for (let i = 0; i < 3; i++) {
                    deleteItem('add')
                }
                putVar('switch', '关');
            } else {
                addArray = [{
                    desc: '解析名称，一行一个',
                    col_type: 'input',
                    extra: {
                        titleVisible: false,
                        type: 'textarea',
                        height: -1,
                        highlight: true,
                        onChange: "putVar('nameArray',input)",
                        id: 'add'
                    }
                }, {
                    desc: '解析，一行一个',
                    col_type: 'input',
                    extra: {
                        titleVisible: false,
                        type: 'textarea',
                        height: -1,
                        highlight: true,
                        onChange: "putVar('urlArray',input)",
                        id: 'add'
                    }
                }, {
                    title: '保存',
                    url: $().lazyRule(() => {
                        nameArray = getVar('nameArray').split(/\r\n|\n|\r/);
                        urlArray = getVar('urlArray').split(/\r\n|\n|\r/);
                        dataLength = nameArray.length > urlArray.length ? nameArray.length : urlArray.length;
                        setFile = readFile('hiker://files/rules/cy/MyParse.json');
                        eval('configArray=' + setFile);
                        for (let i = 0; i < dataLength; i++) {
                            jiexiName = i > nameArray.length - 1 ? '未命名' : (nameArray[i] == '' ? '未命名' : nameArray[i]);
                            jiexiUrl = i > urlArray.length - 1 ? '空解析' : (urlArray[i] == '' ? '空解析' : urlArray[i]);
                            configArray.unshift({
                                name: jiexiName,
                                url: jiexiUrl,
                                tags: '全部'
                            });
                        }
                        saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
                        putVar('switch', '关');
                        refreshPage(true);
                        return "toast://保存成功"
