/*
 vipUrl:官网地址;返回值JSON格式的多线路视频地址
调用接口：$.require("hiker://page/mulParse?rule=配置助手").mulParse(vipUrl);
 */
$.exports.mulParse = function (vipUrl, bfLink, jiexiName,headers) {
    if (vipUrl.indexOf('douban') != -1) {
        vipUrl = vipUrl.split('?ptag=')[0]
    }
    log('视频原地址：' + vipUrl);
    var extra_length = 0;
    if (bfLink && jiexiName) {
        var urls = bfLink;
        var names = jiexiName;
        if (headers) {
            var headers=headers;
        }else{
            var headers=[];
        }
        extra_length = bfLink.length;
    } else {
        var urls = [];
        var names = [];
        var headers=[];
    }
    log("多线路解析开始")
    var list = JSON.parse(readFile("hiker://files/rules/cy/MyParse.json"));
    var tagsArray = JSON.parse(request('hiker://files/rules/cy/MyParse_selection.json'));
    tagsArray.forEach(item => {
        eval('var reg1=/' + item + '/;');
        eval('var reg2=/' + item + '|全部/;');
        if (vipUrl.match(reg1)) {
            log('当前线路:' + item)
            list.forEach(item => {
                if (item.tags == undefined || item.tags.match(reg2)) {
                    urls.push("video://" + item.url + vipUrl);
                    names.push(item.name);
                    headers.push({});
                }
            });
        }
    });
    if (urls.length == 0 || urls.length == extra_length) {
        log('当前线路:other')
        list.forEach(item => {
            urls.push("video://" + item.url + vipUrl);
            names.push(item.name);
        });
    }
    log(`当前启用解析：${urls.length}个`)
    var dmSwitch = request('hiker://files/rules/cy/dmSwitch.txt');
    var s = request('hiker://files/rules/cy/boxSwitch.txt')
    if (s == "开") {
        var u = []
        list.forEach(item => {
            u.push({
                name: item.name,
                url: item.url + vipUrl
            })
        });
        require("https://hikerfans.com/Joe/TVbox.js")
        return 推送tvbox({
            name: "配置助手推送",
            vod: [{
                //name: "默认",
                urls: u
            }]
        })
    } else if (dmSwitch == 'true') {
        return $.require("hiker://page/lazyToJiexi?rule=dm盒子").videoX5(vipUrl, urls, names,headers);
    } else {
        return JSON.stringify({
            urls: urls,
            names: names,
            headers:headers
        });
    }
}

