$.exports.op_basic = function (i) {
    setFile = readFile('hiker://files/rules/cy/MyParse.json');
    eval('var configArray=' + setFile);
    if (configArray[i].tags == undefined) {
        configArray[i].tags = '全部'
    }
    if (input == '编辑') {
        config = configArray[i].name + ',' + configArray[i].url + '###' + configArray[i].tags;
        return $(config).input((i, configArray) => {
            item = {
                name: input.split(/,|，/)[0],
                url: input.split(/###/)[0].split(/,|，/)[1],
                tags: input.split(/###/)[1],
                js: configArray[i].js
            }
            configArray.splice(i, 1, item);
            saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
            refreshPage(false);
            return "toast://更新成功"
        }, i, configArray)
    } else if (input == '移位') {
        return $('0', '你想排第几？序号从0开始~').input((i, configArray) => {
            if (input > configArray.length - 1) {
                input = configArray.length - 1;
            } else if (input < 0) {
                input = 0;
            }
            temp = configArray[i];
            if (i > input) {
                for (let index = i; index > input; index--) {
                    configArray[index] = configArray[index - 1];
                }
            } else {
                for (let index = i; index < input; index++) {
                    configArray[index] = configArray[index + 1];
                }
            }
            configArray[input] = temp;
            saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
            refreshPage(false);
            return "toast://移位成功"
        }, i, configArray)
    } else {
        configArray.splice(i, 1);
        saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
        refreshPage(false);
        return "toast://删除成功"
    }
}

$.exports.dmSettings = function (tag) {
    switch (tag) {
        // 函数切换
        case 0:
            options = ['左岸官弹', 'fc官弹', '三方扶风'];
            return $(options, 1).select(() => {
                curSettings = JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
                curSettings.dmFrom = input;
                saveFile('hiker://files/dm盒子/settings.json', JSON.stringify(curSettings));
                return "toast://切换来源为:" + input;
            })
            break;

        // 更改函数作用域
        case 1:
            options = ['global', 'other'];
            return $(options, 1).select(() => {
                curSettings = JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
                curSettings.dmScope = input;
                saveFile('hiker://files/dm盒子/settings.json', JSON.stringify(curSettings));
                return "toast://切换作用域为:" + input;
            })
            break;
        case 2:
            fontSize = JSON.parse(readFile('hiker://files/dm盒子/settings.json')).fontSize;
            return $(fontSize, '请输入一个合适的数字').input(() => {
                curSettings = JSON.parse(readFile('hiker://files/dm盒子/settings.json'));
                curSettings.fontSize = input;
                saveFile('hiker://files/dm盒子/settings.json', JSON.stringify(curSettings));
                return "toast://弹幕大小已修改为" + input;
            })
            break;    
        default:
            break;
    }
}

