        return $(config).input((i, configArray) => {
            item = {
                name: input.split(/,|，/)[0],
                url: input.split(/###/)[0].split(/,|，/)[1],
                tags: input.split(/###/)[1],
                js: configArray[i].js
            }
            configArray.splice(i, 1, item);
            saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
            refreshPage(false);
            return "toast://更新成功"
        }, i, configArray)
    } else if (input == '移位') {
        return $('0', '你想排第几？序号从0开始~').input((i, configArray) => {
            if (input > configArray.length - 1) {
                input = configArray.length - 1;
            } else if (input < 0) {
                input = 0;
            }
            temp = configArray[i];
            if (i > input) {
                for (let index = i; index > input; index--) {
                    configArray[index] = configArray[index - 1];
                }
            } else {
                for (let index = i; index < input; index++) {
                    configArray[index] = configArray[index + 1];
                }
            }
            configArray[input] = temp;
            saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
            refreshPage(false);
            return "toast://移位成功"
        }, i, configArray)
    } else {
        configArray.splice(i, 1);
        saveFile('hiker://files/rules/cy/MyParse.json', JSON.stringify(configArray));
        refreshPage(false);
        return "toast://删除成功"
    }
}

